  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:0, body:"" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:1, body:"[Chorus 1: Kanye West]<br>" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:2, body:"I gotta testify<br>Come up in the spot lookin' extra fly<br>'Fore the day I die, I'ma touch the sky<br>Gotta testify<br>Come up in the spot lookin' extra fly<br>'Fore the day I die, I'ma touch the sky" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:3, body:"Back when they thought pink Polos would hurt the Roc" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:4, body:"Before Cam got the shit to pop" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:5, body:"The doors was closed<br>I felt like Bad Boy's street team: I couldn't work the locks" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:6, body:"Now let's go, take 'em back to the plan<br>Me and my momma hopped in that U-Haul van" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:7, body:"Let's take 'em back to the club<br>Least about an hour I stand on line<br>I just wanted to dance," )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:8, body:"I went to Jacob an hour<br>After I got my advance, I just wanted to shine" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:9, body:"Jay favorite line: \"Dawg, in due time!\"<br>Now he look at me, like, \"Damn, dawg! You where I am!\"<br>A hip-hop legend" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:10, body:"I think I died in that accident, ‘cause this must be Heaven" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:11, body:"I gotta testify<br>Come up in the spot lookin' extra fly<br>'Fore the day I die, I'ma touch the sky<br>Gotta testify<br>Come up in the spot lookin' extra fly<br>'Fore the day I die, I'ma touch the sky" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:12, body:"A-la-la-la-lah-la-lahhhhh<br>(Top of the world, baby, on top of the world!)" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:13, body:"[Verse 2: Kanye West]" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:14, body:"Back when Gucci was the shit to rock" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:15, body:"Back when Slick Rick got the shit to pop" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:16, body:"Before anybody wanted K. West beats<br>Me and my girl split the buffet at KFC" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:17, body:"Dog, I was havin' nervous breakdowns<br>Like \"Man, these niggas that much better than me?\"" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:18, body:"Baby, I'm goin' on an aeroplane<br>And I don't know if I'll be back again" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:19, body:"Couldn't keep it at home, thought I needed a Nia Long")
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:20, body:"I'm tryin' to right my wrongs<br>But it's funny them same wrongs helped me write this song")
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:21, body:"I gotta testify<br>Come up in the spot lookin' extra fly<br>'Fore the day you die, you gon' touch the sky<br>You gon' touch the sky, baby girl, testify<br>Come up in the spot lookin' extra fly<br>'Fore the day you die, you gon' touch the sky" )
  Verse.create!( song_id: (Song.find_by! name:"Touch the Sky", album: late_registration).id, verse_number:22, body:"[Verse 3: Lupe Fiasco]" )
